con:configuration.scriptText:
import com.eviware.soapui.support.GroovyUtils

def requestxml=context.expand('${SearchById#Request}')
def gUtil=new GroovyUtils(context)
def xmlholder1=gUtil.getXmlHolder(requestxml)
def requestcustomerid=xmlholder1.getNodeValue("//*:customerId")

def responsexml=context.expand('${SearchById#Response}')

def xmlholder2=gUtil.getXmlHolder(responsexml)
def responsecustomerid=xmlholder2.getNodeValue("//*:id")

if (requestcustomerid.toInteger()==responsecustomerid.toInteger())
log.info("got a valid output")
else
log.info("test failed")

con:config.script:
import com.eviware.soapui.support.GroovyUtils

//declaring variable and importing properties

def customerId=731


//importing request property of add request
def searchbyIdReqXml=context.expand('${SearchById#Request}')

//creating xml holder
def gUtil=new GroovyUtils(context)
def xmlhol=gUtil.getXmlHolder(searchbyIdReqXml)

//Changing value of email
//xmlhol.namespaces['ns1']="http://tempuri.org/"
xmlhol.setNodeValue("//*:customerId",customerId)


//set modified xml value to the searchbyid#request property
def modifiedXmlAsString=xmlhol.getXml()


testRunner.testCase.getTestStepByName("SearchById").setPropertyValue("Request",modifiedXmlAsString)

//get current testsuite and testcase name
def currenttestcase=context.getTestCase().name
def suitename=context.getTestCase().getTestSuite().name

//run search by id request Step
testRunner.testCase.testSuite.project.testSuites[suitename].testCases[currenttestcase].testSteps["SearchById"].run(testRunner,context)


//get the respnce property from add request
def searchbyidResXml=context.expand('${SearchById#Response}')
//creating xml holder
//def gUtil=new GroovyUtils(context)
def xmlhol1=gUtil.getXmlHolder(searchbyidResXml)

//create an xml holder to get addition result
//xmlhol1.namespaces['ns1']="http://tempuri.org/"
def result=xmlhol1.getNodeValue("//*:id")
log.info(result)

